import React, { useState } from 'react';

const TrueFalseQuestion = ({ question, onAnswer }) => {
  const [answered, setAnswered] = useState(false);  // Gère si l'utilisateur a répondu
  const [resultMessage, setResultMessage] = useState('');  // Message personnalisé

  const handleAnswer = (isTrue) => {
    setAnswered(true);  // L'utilisateur a répondu
    if (isTrue === question.correct) {
      setResultMessage('Bravo ! Vous avez raison.');
      onAnswer(true);  // Indique que la réponse est correcte
    } else {
      setResultMessage('Dommage ! Ce n’est pas la bonne réponse.');
      onAnswer(false);  // Indique que la réponse est incorrecte
    }
  };

  return (
    <div className="true-false-question">
      <p>{question.text}</p>
      {!answered ? (
        <div>
          <button onClick={() => handleAnswer(true)}>Vrai</button>
          <button onClick={() => handleAnswer(false)}>Faux</button>
        </div>
      ) : (
        <div>
          <p>{resultMessage}</p>
        </div>
      )}
    </div>
  );
};

export default TrueFalseQuestion;
