import React, { useState } from 'react';
import NarrativeSequence from './components/NarrativeSequence';
import VideoSequence from './components/VideoSequence';
import TrueFalseQuestion from './components/TrueFalseQuestion';
import InteractiveMap from './components/InteractiveMap';

const App = () => {
  const [step, setStep] = useState(0);
  const [score, setScore] = useState(0);

  const handleNarrativeEnd = () => setStep(step + 1);
  const handleVideoEnd = () => setStep(step + 1);

  const handleAnswer = (correct) => {
    if (correct) {
      setScore(score + 1);
    }
    setStep(step + 1);
  };

  const handleLocationReached = (location) => {
    console.log(`Arrived at ${location}`);
    setStep(step + 1);
  };

  const narrativeTexts = [
    "Le petit alien vagabonde entre les galaxies.",
    "Avant de continuer son voyage, le petit Alien souhaite faire une halte sur la planète la plus proche avant de repartir.",
    "C'est là qu’il trouva la ✨Terre ✨."
  ];

  const narrativeTextVideo = [
    "Il atterrit sur un îlot entouré par l’océan, il s’agit de la mythique cité d’Atlantide pendant son âge d’or."
  ];

  const trueFalseQuestion = {
    text: "Le monde est plat. Vrai ou Faux ?",
    correct: false
  };

  const locations = ['Paris', 'New York', 'Tokyo'];

  return (
    <div className="container">
      {step === 0 && <NarrativeSequence texts={narrativeTexts} onNext={handleNarrativeEnd} />}
      {step === 1 && <VideoSequence videoSrc="/videos/video1.mp4" onEnd={handleVideoEnd} />}
      {step === 2 && <TrueFalseQuestion question={trueFalseQuestion} onAnswer={handleAnswer} score={score} />}
      {step === 3 && <InteractiveMap locations={locations} onReachLocation={handleLocationReached} />}
    </div>
  );
};

export default App;
